import React, { useState } from 'react';
import RecipeCard from './components/RecipeCard';
import './App.css';

function App() {
  const [recipes, setRecipes] = useState([
    {
      id: 1,
      title: 'Gâteau au Chocolat',
      prepTime: '45 minutes',
      ingredients: [
        '200g de chocolat',
        '150g de farine',
        '100g de sucre',
        '3 œufs'
      ],
      instructions: [
        'Préchauffer le four à 180°C',
        'Faire fondre le chocolat',
        'Mélanger tous les ingrédients',
        'Cuire pendant 30 minutes'
      ]
    }
  ]);

  const [newRecipe, setNewRecipe] = useState({
    title: '',
    prepTime: '',
    ingredients: [''],
    instructions: ['']
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewRecipe(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleIngredientChange = (index, value) => {
    const newIngredients = [...newRecipe.ingredients];
    newIngredients[index] = value;
    setNewRecipe(prev => ({
      ...prev,
      ingredients: newIngredients
    }));
  };

  const handleInstructionChange = (index, value) => {
    const newInstructions = [...newRecipe.instructions];
    newInstructions[index] = value;
    setNewRecipe(prev => ({
      ...prev,
      instructions: newInstructions
    }));
  };

  const addIngredient = () => {
    setNewRecipe(prev => ({
      ...prev,
      ingredients: [...prev.ingredients, '']
    }));
  };

  const addInstruction = () => {
    setNewRecipe(prev => ({
      ...prev,
      instructions: [...prev.instructions, '']
    }));
  };

  const addRecipe = (e) => {
    e.preventDefault();
    const recipeToAdd = {
      ...newRecipe,
      id: recipes.length + 1
    };
    setRecipes(prev => [...prev, recipeToAdd]);
    setNewRecipe({
      title: '',
      prepTime: '',
      ingredients: [''],
      instructions: ['']
    });
  };

  const deleteRecipe = (id) => {
    setRecipes(prev => prev.filter(recipe => recipe.id !== id));
  };

  return (
    <div className="App">
      <h1>Mes Recettes</h1>
      <div className="recipe-form">
        <form onSubmit={addRecipe}>
          <input
            type="text"
            name="title"
            placeholder="Titre de la recette"
            value={newRecipe.title}
            onChange={handleInputChange}
            required
          />
          <input
            type="text"
            name="prepTime"
            placeholder="Temps de préparation"
            value={newRecipe.prepTime}
            onChange={handleInputChange}
            required
          />

          <h3>Ingrédients</h3>
          {newRecipe.ingredients.map((ingredient, index) => (
            <input
              key={index}
              type="text"
              placeholder={`Ingrédient ${index + 1}`}
              value={ingredient}
              onChange={(e) => handleIngredientChange(index, e.target.value)}
              required
            />
          ))}
          <button type="button" onClick={addIngredient}>+ Ajouter un ingrédient</button>

          <h3>Instructions</h3>
          {newRecipe.instructions.map((instruction, index) => (
            <input
              key={index}
              type="text"
              placeholder={`Étape ${index + 1}`}
              value={instruction}
              onChange={(e) => handleInstructionChange(index, e.target.value)}
              required
            />
          ))}
          <button type="button" onClick={addInstruction}>+ Ajouter une étape</button>

          <button type="submit">Ajouter la recette</button>
        </form>
      </div>

      <div className="recipe-list">
        {recipes.map(recipe => (
          <RecipeCard 
            key={recipe.id} 
            recipe={recipe} 
            onDelete={deleteRecipe} 
          />
        ))}
      </div>
    </div>
  );
}

export default App;
