import React, { useState, useEffect } from 'react';
import { supabase } from '../supabaseClient';
import './RecipeCard.css';

const RecipeCardNew = () => {
  const [recipe, setRecipe] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchRecipe = async () => {
      try {
        // Fetch a single recipe (you might want to pass an ID or use a different fetching strategy)
        const { data, error } = await supabase
          .from('recipes')
          .select('*')
          .single(); // Fetch a single recipe

        if (error) throw error;

        setRecipe(data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching recipe:', err);
        setError(err);
        setLoading(false);
      }
    };

    fetchRecipe();
  }, []);

  if (loading) return <div className="recipe-card-loading">Chargement...</div>;
  if (error) return <div className="recipe-card-error">Erreur de chargement</div>;
  if (!recipe) return <div className="recipe-card-empty">Aucune recette trouvée</div>;

  return (
    <div className="recipe-card">
      <div className="recipe-card-image">
        <img 
          src={recipe.imageUrl || 'https://via.placeholder.com/500x200'} 
          alt={recipe.title} 
          onError={(e) => {
            e.target.src = 'https://via.placeholder.com/500x200';
          }}
        />
      </div>

      <div className="recipe-card-content">
        <div className="recipe-card-glass-overlay"></div>
        <div className="recipe-card-inner-content">
          {/* Titre de la recette */}
          <h3 className="recipe-title">{recipe.title}</h3>

          {/* Section Temps de préparation */}
          <div className="recipe-prep-time">
            <strong>Temps de préparation :</strong>
            <p>{recipe.prepTime || 'Non spécifié'}</p>
          </div>

          {/* Section Ingrédients */}
          <div className="recipe-ingredients">
            <strong>Ingrédients :</strong>
            <ul>
              {recipe.ingredients && recipe.ingredients.length > 0 ? (
                recipe.ingredients.map((ingredient, index) => (
                  <li key={index}>{ingredient}</li>
                ))
              ) : (
                <li>Aucun ingrédient</li>
              )}
            </ul>
          </div>

          {/* Section Instructions */}
          <div className="recipe-instructions">
            <strong>Instructions :</strong>
            <ol>
              {recipe.instructions && recipe.instructions.length > 0 ? (
                recipe.instructions.map((step, index) => (
                  <li key={index}>{step}</li>
                ))
              ) : (
                <li>Aucune instruction</li>
              )}
            </ol>
          </div>

          {/* Bouton de suppression */}
          <button 
            className="delete-btn"
            onClick={() => {
              // Logique de suppression à implémenter
              console.log('Supprimer la recette', recipe.id);
            }}
          >
            Supprimer
          </button>
        </div>
      </div>
    </div>
  );
};

export default RecipeCardNew;
