import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import HomePage from './pages/HomePage';
import NewRecipePage from './pages/NewRecipePage';
import './App.css';

function App() {
  // Charger les recettes depuis localStorage ou utiliser un tableau par défaut
  const [recipes, setRecipes] = useState(() => {
    const savedRecipes = localStorage.getItem('recipes');
    return savedRecipes 
      ? JSON.parse(savedRecipes) 
      : [
          {
            id: 1,
            title: 'Gâteau au Chocolat',
            prepTime: '45 minutes',
            ingredients: [
              '200g de chocolat',
              '150g de farine',
              '100g de sucre',
              '3 œufs'
            ],
            instructions: [
              'Préchauffer le four à 180°C',
              'Faire fondre le chocolat',
              'Mélanger tous les ingrédients',
              'Cuire pendant 30 minutes'
            ]
          }
        ];
  });

  // Sauvegarder les recettes dans localStorage à chaque modification
  useEffect(() => {
    localStorage.setItem('recipes', JSON.stringify(recipes));
  }, [recipes]);

  const addRecipe = (newRecipe) => {
    const recipeToAdd = {
      ...newRecipe,
      id: Date.now() // Utiliser un timestamp comme ID unique
    };
    setRecipes(prev => [...prev, recipeToAdd]);
  };

  const deleteRecipe = (id) => {
    setRecipes(prev => prev.filter(recipe => recipe.id !== id));
  };

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route 
            path="/" 
            element={<HomePage recipes={recipes} onDelete={deleteRecipe} />} 
          />
          <Route 
            path="/new-recipe" 
            element={<NewRecipePage onAddRecipe={addRecipe} />} 
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
