import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import HomePage from './pages/HomePage';
import NewRecipePage from './pages/NewRecipePage';
import initialRecipesData from './data/recipes.json';
import './App.css';

function App() {
  // Charger les recettes depuis localStorage ou utiliser le fichier JSON par défaut
  const [recipes, setRecipes] = useState(() => {
    const savedRecipes = localStorage.getItem('recipes');
    return savedRecipes 
      ? JSON.parse(savedRecipes) 
      : initialRecipesData.recipes;
  });

  // État pour la recette en plein écran
  const [fullScreenRecipe, setFullScreenRecipe] = useState(null);

  // Sauvegarder les recettes dans localStorage à chaque modification
  useEffect(() => {
    localStorage.setItem('recipes', JSON.stringify(recipes));
  }, [recipes]);

  const addRecipe = (newRecipe) => {
    const recipeToAdd = {
      ...newRecipe,
      id: Date.now(), // Utiliser un timestamp comme ID unique
      imageUrl: newRecipe.imageUrl || 'https://via.placeholder.com/500x200' // Image par défaut si non fournie
    };
    setRecipes(prev => [...prev, recipeToAdd]);
  };

  const deleteRecipe = (id) => {
    setRecipes(prev => prev.filter(recipe => recipe.id !== id));
    // Fermer la vue plein écran si la recette supprimée est en plein écran
    if (fullScreenRecipe && fullScreenRecipe.id === id) {
      setFullScreenRecipe(null);
    }
  };

  const openFullScreen = (recipe) => {
    setFullScreenRecipe(recipe);
  };

  const closeFullScreen = () => {
    setFullScreenRecipe(null);
  };

  return (
    <Router>
    <div className="App">
        <Routes>
          <Route 
            path="/" 
            element={
              <HomePage 
                recipes={recipes} 
                onDelete={deleteRecipe} 
                onOpenFullScreen={openFullScreen}
              />
            } 
          />
          <Route 
            path="/new-recipe" 
            element={<NewRecipePage onAddRecipe={addRecipe} />} 
          />
        </Routes>

        {/* Overlay plein écran */}
        {fullScreenRecipe && (
          <div className="full-screen-overlay" onClick={closeFullScreen}>
            <div 
              className="full-screen-recipe" 
              onClick={(e) => e.stopPropagation()}
            >
              <button 
                className="close-full-screen-btn" 
                onClick={closeFullScreen}
              >
                ✕
              </button>
              <div className="full-screen-recipe-content">
                <div className="full-screen-recipe-image">
                  <img 
                    src={fullScreenRecipe.imageUrl} 
                    alt={fullScreenRecipe.title} 
                  />
                </div>
                <div className="full-screen-recipe-details">
                  <h2>{fullScreenRecipe.title}</h2>
                  <p><strong>Temps de préparation :</strong> {fullScreenRecipe.prepTime}</p>

                  <div className="full-screen-section">
                    <h3>Ingrédients</h3>
                    <ul>
                      {fullScreenRecipe.ingredients.map((ingredient, index) => (
                        <li key={index}>{ingredient}</li>
                      ))}
                    </ul>
                  </div>

                  <div className="full-screen-section">
                    <h3>Instructions</h3>
                    <ol>
                      {fullScreenRecipe.instructions.map((step, index) => (
                        <li key={index}>{step}</li>
                      ))}
                    </ol>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}
    </div>
    </Router>
  );
}

export default App;
