import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import RecipeCardNew from '../components/RecipeCardNew';
import { Link } from 'react-router-dom';
import './HomePage.css';

const HomePage = ({ recipes, onDelete, onOpenFullScreen, onEdit }) => {
  const navigate = useNavigate();
  const [selectedRecipe, setSelectedRecipe] = useState(null);

  const handleEditRecipe = (recipe) => {
    // Navigate to the new recipe page with the recipe data
    navigate('/new-recipe', { 
      state: { 
        recipe: {
          ...recipe,
          // Ensure all fields are passed
          ingredients: recipe.ingredients || [],
          instructions: recipe.instructions || [],
          price: recipe.price || ''
        } 
      } 
    });
  };

  return (
    <div className="home-page">
      <h1>Mes Recettes</h1>

      <Link to="/new-recipe" className="add-recipe-btn">
        Ajouter une nouvelle recette
      </Link>

      <div className="recipe-list">
        {recipes.map((recipe) => (
          <div key={recipe.id} className="recipe-card-container">
            <RecipeCardNew 
              recipe={recipe} 
              onDelete={onDelete}
              onOpenFullScreen={onOpenFullScreen}
            />
            <button 
              className="edit-recipe-btn"
              onClick={() => handleEditRecipe(recipe)}
            >
              Modifier
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HomePage;
