import React, { useState, useEffect } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import './MealPlanningPage.css';

function MealPlanningPage({ recipes }) {
  const [activeView, setActiveView] = useState('day');
  const [daySlots, setDaySlots] = useState([null, null, null]);

  // Drag and drop handler
  const onDragEnd = (result) => {
    const { source, destination } = result;

    // If dropped outside a droppable area
    if (!destination) return;

    // If dropped in the same slot, do nothing
    if (source.droppableId === destination.droppableId && 
        source.index === destination.index) return;

    // Create a new day slots array
    const newDaySlots = [...daySlots];

    // Remove the recipe from its source slot
    const [movedRecipe] = newDaySlots.splice(source.index, 1, null);

    // Place the recipe in the destination slot
    if (destination.index !== -1) {
      newDaySlots[destination.index] = movedRecipe;
    }

    setDaySlots(newDaySlots);
  };

  return (
    <div className="meal-planning-page">
      <h1>Meal Planning</h1>

      {/* Navigation Pills */}
      <div className="meal-planning-nav">
        <button 
          className={`meal-planning-pill ${activeView === 'day' ? 'active' : ''}`}
          onClick={() => setActiveView('day')}
        >
          Jour
        </button>
        <button 
          className={`meal-planning-pill ${activeView === 'week' ? 'active' : ''}`}
          onClick={() => setActiveView('week')}
        >
          Semaine
        </button>
        <button 
          className={`meal-planning-pill ${activeView === 'month' ? 'active' : ''}`}
          onClick={() => setActiveView('month')}
        >
          Mois
        </button>
      </div>

      {/* Day View */}
      {activeView === 'day' && (
        <div className="day-view">
          <div className="day-card">
            <h2>Aujourd'hui</h2>
            <DragDropContext onDragEnd={onDragEnd}>
              <div className="day-slots">
                {daySlots.map((slot, index) => (
                  <Droppable droppableId={`slot-${index}`} key={`slot-${index}`}>
                    {(provided) => (
                      <div 
                        className="day-slot" 
                        {...provided.droppableProps}
                        ref={provided.innerRef}
                      >
                        {slot ? (
                          <Draggable draggableId={`recipe-${slot.id}`} index={index}>
                            {(provided) => (
                              <div
                                ref={provided.innerRef}
                                {...provided.draggableProps}
                                {...provided.dragHandleProps}
                                className="day-slot-recipe"
                              >
                                <img 
                                  src={slot.imageUrl || 'https://via.placeholder.com/150'} 
                                  alt={slot.title} 
                                />
                                <p>{slot.title}</p>
                              </div>
                            )}
                          </Draggable>
                        ) : (
                          <p>Glisser une recette ici</p>
                        )}
                        {provided.placeholder}
                      </div>
                    )}
                  </Droppable>
                ))}
              </div>
            </DragDropContext>
          </div>
        </div>
      )}

      {/* Week and Month Views (Placeholder) */}
      {(activeView === 'week' || activeView === 'month') && (
        <div className="placeholder-view">
          <p>Vue {activeView} en développement</p>
        </div>
      )}

      {/* Recipe List */}
      <div className="meal-planning-recipe-list">
        <h2>Toutes les Recettes</h2>
        <DragDropContext onDragEnd={onDragEnd}>
          <Droppable droppableId="recipe-list" direction="horizontal">
            {(provided) => (
              <div 
                className="recipe-list-container"
                {...provided.droppableProps}
                ref={provided.innerRef}
              >
                {recipes.map((recipe, index) => (
                  <Draggable 
                    key={recipe.id} 
                    draggableId={`recipe-${recipe.id}`} 
                    index={index}
                  >
                    {(provided) => (
                      <div
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                        className="draggable-recipe-card"
                      >
                        <img 
                          src={recipe.imageUrl || 'https://via.placeholder.com/150'} 
                          alt={recipe.title} 
                        />
                        <p>{recipe.title}</p>
                      </div>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>
        </DragDropContext>
      </div>
    </div>
  );
}

export default MealPlanningPage;
