import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function NewRecipePage({ onAddRecipe }) {
  const navigate = useNavigate();
  const [newRecipe, setNewRecipe] = useState({
    title: '',
    prepTime: '',
    ingredients: [''],
    instructions: ['']
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewRecipe(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleIngredientChange = (index, value) => {
    const newIngredients = [...newRecipe.ingredients];
    newIngredients[index] = value;
    setNewRecipe(prev => ({
      ...prev,
      ingredients: newIngredients
    }));
  };

  const handleInstructionChange = (index, value) => {
    const newInstructions = [...newRecipe.instructions];
    newInstructions[index] = value;
    setNewRecipe(prev => ({
      ...prev,
      instructions: newInstructions
    }));
  };

  const addIngredient = () => {
    setNewRecipe(prev => ({
      ...prev,
      ingredients: [...prev.ingredients, '']
    }));
  };

  const addInstruction = () => {
    setNewRecipe(prev => ({
      ...prev,
      instructions: [...prev.instructions, '']
    }));
  };

  const addRecipe = (e) => {
    e.preventDefault();
    onAddRecipe(newRecipe);
    navigate('/');
  };

  return (
    <div className="new-recipe-page">
      <h1>Créer une nouvelle recette</h1>
      <div className="recipe-form">
        <form onSubmit={addRecipe}>
          <input
            type="text"
            name="title"
            placeholder="Titre de la recette"
            value={newRecipe.title}
            onChange={handleInputChange}
            required
          />
          <input
            type="text"
            name="prepTime"
            placeholder="Temps de préparation"
            value={newRecipe.prepTime}
            onChange={handleInputChange}
            required
          />

          <h3>Ingrédients</h3>
          {newRecipe.ingredients.map((ingredient, index) => (
            <input
              key={index}
              type="text"
              placeholder={`Ingrédient ${index + 1}`}
              value={ingredient}
              onChange={(e) => handleIngredientChange(index, e.target.value)}
              required
            />
          ))}
          <button type="button" onClick={addIngredient}>+ Ajouter un ingrédient</button>

          <h3>Instructions</h3>
          {newRecipe.instructions.map((instruction, index) => (
            <input
              key={index}
              type="text"
              placeholder={`Étape ${index + 1}`}
              value={instruction}
              onChange={(e) => handleInstructionChange(index, e.target.value)}
              required
            />
          ))}
          <button type="button" onClick={addInstruction}>+ Ajouter une étape</button>

          <div className="form-actions">
            <button type="submit">Ajouter la recette</button>
            <button type="button" onClick={() => navigate('/')}>Annuler</button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default NewRecipePage;
